import React from "react";
import { Card, Stat } from "../../components/UI";

function TrainerDashboard({ auth }) {
  const users = JSON.parse(localStorage.getItem("users") || "[]");
  const clients = users.filter(u => (auth.clients || []).includes(u.id));
  const workouts = JSON.parse(localStorage.getItem("workouts") || "[]").filter(w => clients.map(c => c.id).includes(w.userId));
  const diets = JSON.parse(localStorage.getItem("diets") || "[]").filter(d => clients.map(c => c.id).includes(d.userId));

  return (
    <div className="container">
      <h2>Welcome, {auth.name}</h2>
      <div className="row" style={{ marginTop: 12 }}>
        <div className="col"><Stat label="Clients" value={clients.length} /></div>
        <div className="col"><Stat label="Assigned Workouts" value={workouts.length} /></div>
        <div className="col"><Stat label="Diet Plans" value={diets.length} /></div>
      </div>
      <div className="row" style={{ marginTop: 12 }}>
        <div className="col">
          <Card title="Trainer Actions">
            <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
              <a href="/trainer/workouts"><button>Create Workouts</button></a>
              <a href="/trainer/diets"><button>Create Diet Plans</button></a>
              <a href="/trainer/progress"><button>View Progress</button></a>
              <a href="/trainer/bmi"><button>View BMI Reports</button></a>
            </div>
          </Card>
        </div>
      </div>
    </div>
  );
}

export default TrainerDashboard;
