import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

function LoginForm({ onLogin }) {
  const [email, setEmail] = useState("harini@example.com");
  const [password, setPassword] = useState("password");
  const [role, setRole] = useState("user");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Mock auth: find in localStorage users/trainers by email & role
    const store = role === "user" ? "users" : "trainers";
    const list = JSON.parse(localStorage.getItem(store) || "[]");
    const found = list.find((x) => x.email === email);

    if (!found) {
      alert("No account found. Please register.");
      return;
    }
    // For demo, we ignore password; in real app, verify with backend
    onLogin(found);
    navigate(role === "user" ? "/user" : "/trainer");
  };

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: 420, margin: "40px auto" }}>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <label>Email</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

          <label>Password</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />

          <label>Role</label>
          <select value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="user">User</option>
            <option value="trainer">Trainer</option>
          </select>

          <div style={{ display: "flex", gap: 8, marginTop: 12 }}>
            <button type="submit">Login</button>
            <Link to="/register"><button type="button" className="btn-secondary">Register</button></Link>
          </div>
        </form>
        <p className="helper" style={{ marginTop: 8 }}>
          For demo: user "harini@example.com", trainer "arun.trainer@example.com"
        </p>
      </div>
    </div>
  );
}

export default LoginForm;
