import React, { useEffect, useState } from "react";

function UserDietPlans({ auth }) {
  const [diets, setDiets] = useState([]);

  useEffect(() => {
    const list = JSON.parse(localStorage.getItem("diets") || "[]").filter(x => x.userId === auth.id);
    setDiets(list);
  }, [auth.id]);

  return (
    <div className="container">
      <h2>My Diet Plans</h2>
      <div className="card" style={{ marginTop: 12 }}>
        <table style={{ width: "100%" }}>
          <thead>
            <tr><th align="left">Meal</th><th align="left">Food Item</th><th align="left">Calories</th></tr>
          </thead>
          <tbody>
            {diets.map(d => (
              <tr key={d.id}>
                <td>{d.mealType}</td>
                <td>{d.foodItem}</td>
                <td>{d.calories}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {!diets.length && <div style={{ padding: 12 }}>No diet plans assigned.</div>}
      </div>
      <div style={{ marginTop: 20 }}>
              <DietPieChart userId={auth.id} />
        </div>
    </div>
  );
}

export default UserDietPlans;
