import React, { useEffect, useState } from "react";
import { Card, Stat } from "../../components/UI";

function UserDashboard({ auth }) {
  const [latestBmi, setLatestBmi] = useState("—");
  const [workoutStats, setWorkoutStats] = useState({ total: 0, done: 0 });
  const [dietPreview, setDietPreview] = useState([]);

  useEffect(() => {
    const bmiLogs = JSON.parse(localStorage.getItem("bmiLogs") || "[]").filter(x => x.userId === auth.id);
    setLatestBmi(bmiLogs.length ? bmiLogs[bmiLogs.length - 1].bmi : "—");

    const workouts = JSON.parse(localStorage.getItem("workouts") || "[]").filter(x => x.userId === auth.id);
    setWorkoutStats({ total: workouts.length, done: workouts.filter(w => w.done).length });

    const diets = JSON.parse(localStorage.getItem("diets") || "[]").filter(x => x.userId === auth.id);
    setDietPreview(diets.slice(0, 5)); // preview first 5 items
  }, [auth.id]);

  return (
    <div className="container">
      <h2>Hello, {auth.name}</h2>
      <div className="row" style={{ marginTop: 12 }}>
        <div className="col"><Stat label="Latest BMI" value={latestBmi} /></div>
        <div className="col"><Stat label="Workouts Completed" value={workoutStats.done} /></div>
        <div className="col"><Stat label="Assigned Workouts" value={workoutStats.total} /></div>
      </div>

      <div className="row" style={{ marginTop: 12 }}>
        <div className="col">
          <Card title="Diet chart preview">
            <div className="list">
              {dietPreview.map(d => (
                <div className="list-item" key={d.id}>
                  <div><strong>{d.mealType}</strong> — {d.foodItem}</div>
                  <div>{d.calories} kcal</div>
                </div>
              ))}
              {!dietPreview.length && <div style={{ padding: 12 }}>No diet plans assigned.</div>}
            </div>
          </Card>
        </div>
        <div className="col">
          <Card title="Shortcuts">
            <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
              <a href="/user/workouts"><button>My Workouts</button></a>
              <a href="/user/diets"><button>My Diet</button></a>
              <a href="/user/progress"><button>Upload Progress</button></a>
              <a href="/user/bmi"><button>BMI Reports</button></a>
            </div>
          </Card>
        </div>
      </div>
    </div>
  );
}

export default UserDashboard;
