import React, { useEffect, useState } from "react";

function TrainerProfile({ auth }) {
  const [form, setForm] = useState({
    name: auth.name || "",
    email: auth.email || "",
    specialization: auth.specialization || "",
    availability: "Mon-Fri",
  });
  const [clients, setClients] = useState([]);

  useEffect(() => {
    const users = JSON.parse(localStorage.getItem("users") || "[]");
    const myClients = users.filter(u => (auth.clients || []).includes(u.id));
    setClients(myClients);
  }, [auth.clients]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSave = (e) => {
    e.preventDefault();
    const trainers = JSON.parse(localStorage.getItem("trainers") || "[]");
    const updated = trainers.map(t => t.id === auth.id ? { ...t, name: form.name, email: form.email, specialization: form.specialization } : t);
    localStorage.setItem("trainers", JSON.stringify(updated));
    alert("Trainer profile updated!");
  };

  return (
    <div className="container">
      <h2>Trainer Profile</h2>
      <div className="card" style={{ marginTop: 12 }}>
        <form onSubmit={handleSave}>
          <label>Name</label>
          <input name="name" value={form.name} onChange={handleChange} required />
          <label>Email</label>
          <input type="email" name="email" value={form.email} onChange={handleChange} required />
          <label>Specialization</label>
          <input name="specialization" value={form.specialization} onChange={handleChange} placeholder="e.g., Weight Loss" />
          <label>Availability</label>
          <input name="availability" value={form.availability} onChange={handleChange} />
          <div style={{ marginTop: 12 }}>
            <button type="submit">Save Profile</button>
          </div>
        </form>
      </div>

      <div className="card" style={{ marginTop: 12 }}>
        <h3>Clients</h3>
        <div className="list" style={{ marginTop: 8 }}>
          {clients.map(c => (
            <div className="list-item" key={c.id}>
              <div><strong>{c.name}</strong> <span className="helper">({c.email})</span></div>
              <div className="helper">Age {c.age || "—"} • BMI logs: {(JSON.parse(localStorage.getItem("bmiLogs") || "[]").filter(l => l.userId === c.id)).length}</div>
            </div>
          ))}
          {!clients.length && <div style={{ padding: 12 }}>No clients assigned.</div>}
        </div>
      </div>
    </div>
  );
}

export default TrainerProfile;
