import React, { useEffect, useState } from "react";

function TrainerProgress({ auth }) {
  const [photos, setPhotos] = useState([]);
  const [clients, setClients] = useState([]);

  useEffect(() => {
    const users = JSON.parse(localStorage.getItem("users") || "[]");
    const myClients = users.filter(u => (auth.clients || []).includes(u.id));
    setClients(myClients);

    const all = JSON.parse(localStorage.getItem("progressPhotos") || "[]")
      .filter(p => (auth.clients || []).includes(p.userId));
    setPhotos(all);
  }, [auth.clients]);

  return (
    <div className="container">
      <h2>Client Progress</h2>
      <div className="card" style={{ marginTop: 12 }}>
        <div className="helper">Clients: {clients.map(c => c.name).join(", ") || "None"}</div>
      </div>
      <div className="row" style={{ marginTop: 12 }}>
        {photos.map(p => (
          <div className="col" key={p.id}>
            <div className="card">
              {p.url ? <img src={p.url} alt={p.caption} style={{ width: "100%", borderRadius: 8 }} /> : <div className="helper">No preview</div>}
              <div style={{ marginTop: 6 }}>
                <strong>{(clients.find(c => c.id === p.userId) || {}).name || "Client"}</strong> â€” {p.caption}
              </div>
            </div>
          </div>
        ))}
        {!photos.length && <div className="card">No client photos yet.</div>}
      </div>
    </div>
  );
}

export default TrainerProgress;
