import React, { useEffect, useState } from "react";

function UserProgress({ auth }) {
  const [file, setFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const list = JSON.parse(localStorage.getItem("progressPhotos") || "[]").filter(x => x.userId === auth.id);
    setPhotos(list);
  }, [auth.id]);

  const handleUpload = (e) => {
    e.preventDefault();
    if (!file) return alert("Select an image");
    const reader = new FileReader();
    reader.onload = () => {
      const list = JSON.parse(localStorage.getItem("progressPhotos") || "[]");
      const newItem = { id: "p" + (list.length + 1), userId: auth.id, url: reader.result, caption };
      localStorage.setItem("progressPhotos", JSON.stringify([...list, newItem]));
      setPhotos([...photos, newItem]);
      setFile(null); setCaption("");
      alert("Photo uploaded!");
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="container">
      <h2>My Progress</h2>
      <div className="card" style={{ marginTop: 12 }}>
        <form onSubmit={handleUpload}>
          <label>Upload Transformation Picture</label>
          <input type="file" accept="image/*" onChange={(e) => setFile(e.target.files[0])} required />
          <label>Caption</label>
          <input value={caption} onChange={(e) => setCaption(e.target.value)} placeholder="Week 3 update" />
          <div style={{ marginTop: 12 }}>
            <button type="submit">Upload</button>
          </div>
        </form>
      </div>

      <div className="row" style={{ marginTop: 12 }}>
        {photos.map(p => (
          <div className="col" key={p.id}>
            <div className="card">
              {p.url ? <img src={p.url} alt={p.caption} style={{ width: "100%", borderRadius: 8 }} /> : <div className="helper">No preview</div>}
              <div style={{ marginTop: 6 }}><strong>{p.caption}</strong></div>
            </div>
          </div>
        ))}
        {!photos.length && <div className="card">No photos yet.</div>}
      </div>
    </div>
  );
}

export default UserProgress;
