import React, { useEffect, useState } from "react";

function TrainerDietPlans({ auth }) {
  const [mealType, setMealType] = useState("breakfast");
  const [foodItem, setFoodItem] = useState("");
  const [calories, setCalories] = useState("");
  const [assignedUser, setAssignedUser] = useState("");
  const [clients, setClients] = useState([]);

  useEffect(() => {
    const users = JSON.parse(localStorage.getItem("users") || "[]");
    setClients(users); // ✅ trainer can choose from registered users
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!assignedUser) return alert("Select a user");
    const list = JSON.parse(localStorage.getItem("diets") || "[]");
    const newItem = { id: "d" + (list.length + 1), userId: assignedUser, mealType, foodItem, calories: Number(calories) };
    localStorage.setItem("diets", JSON.stringify([...list, newItem]));
    alert("Diet plan assigned to user!");
    setFoodItem(""); setCalories("");
  };

  return (
    <div className="container">
      <h2>Create Diet Plan</h2>
      <div className="card" style={{ marginTop: 12 }}>
        <form onSubmit={handleSubmit}>
          <label>Meal Type</label>
          <select value={mealType} onChange={(e) => setMealType(e.target.value)}>
            <option value="breakfast">Breakfast</option>
            <option value="lunch">Lunch</option>
            <option value="dinner">Dinner</option>
          </select>
          <label>Food Item</label>
          <input value={foodItem} onChange={(e) => setFoodItem(e.target.value)} required />
          <label>Calories</label>
          <input type="number" value={calories} onChange={(e) => setCalories(e.target.value)} required />
          <label>Assign to User</label>
          <select value={assignedUser} onChange={(e) => setAssignedUser(e.target.value)}>
            <option value="">Select user</option>
            {clients.map(c => <option key={c.id} value={c.id}>{c.name} ({c.email})</option>)}
          </select>
          <div style={{ marginTop: 12 }}>
            <button type="submit">Assign Diet Plan</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default TrainerDietPlans;
