import React, { useState } from "react";

function Register() {
  const [form, setForm] = useState({
    role: "user",
    name: "",
    email: "",
    password: "",
    age: "",
    gender: "",
    height: "",
    weight: "",
    specialization: "",
  });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    const store = form.role === "user" ? "users" : form.role === "trainer" ? "trainers" : "admins";
    const list = JSON.parse(localStorage.getItem(store) || "[]");
    const exists = list.find((x) => x.email === form.email);
    if (exists) return alert("Account already exists.");

    if (form.role === "user") {
      list.push({
        id: "u" + (list.length + 1),
        name: form.name,
        email: form.email,
        role: "user",
        age: Number(form.age) || null,
        gender: form.gender || "",
        height: Number(form.height) || null,
        weight: Number(form.weight) || null,
        goals: "",
      });
    } else if (form.role === "trainer") {
      list.push({
        id: "t" + (list.length + 1),
        name: form.name,
        email: form.email,
        role: "trainer",
        specialization: form.specialization || "General",
        clients: [],
      });
    } else {
      list.push({
        id: "a" + (list.length + 1),
        name: form.name,
        email: form.email,
        role: "admin",
      });
    }

    localStorage.setItem(store, JSON.stringify(list));
    alert("Registered successfully! Please login.");
  };

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: 520, margin: "20px auto" }}>
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <label>Role</label>
          <select name="role" value={form.role} onChange={handleChange}>
            <option value="user">User</option>
            <option value="trainer">Trainer</option>
            <option value="admin">Admin</option>
          </select>

          <label>Name</label>
          <input name="name" value={form.name} onChange={handleChange} required />

          <label>Email</label>
          <input type="email" name="email" value={form.email} onChange={handleChange} required />

          <label>Password</label>
          <input type="password" name="password" value={form.password} onChange={handleChange} required />

          {form.role === "user" ? (
            <>
              <label>Age</label>
              <input type="number" name="age" value={form.age} onChange={handleChange} />
              <label>Gender</label>
              <select name="gender" value={form.gender} onChange={handleChange}>
                <option value="">Select</option>
                <option value="female">Female</option>
                <option value="male">Male</option>
              </select>
              <label>Height (cm)</label>
              <input type="number" name="height" value={form.height} onChange={handleChange} />
              <label>Weight (kg)</label>
              <input type="number" name="weight" value={form.weight} onChange={handleChange} />
            </>
          ) : form.role === "trainer" ? (
            <>
              <label>Specialization</label>
              <input name="specialization" value={form.specialization} onChange={handleChange} placeholder="e.g., Strength" />
            </>
          ) : null}

          <div style={{ marginTop: 12 }}>
            <button type="submit">Register</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Register;
