import React, { useEffect, useState } from "react";
import WorkoutBarChart from "../../components/WorkoutBarChart";


function UserWorkouts({ auth }) {
  const [workouts, setWorkouts] = useState([]);

  useEffect(() => {
    const list = JSON.parse(localStorage.getItem("workouts") || "[]").filter(x => x.userId === auth.id);
    setWorkouts(list);
  }, [auth.id]);

  const markDone = (id) => {
    const list = JSON.parse(localStorage.getItem("workouts") || "[]");
    const updated = list.map(w => w.id === id ? { ...w, done: true } : w);
    localStorage.setItem("workouts", JSON.stringify(updated));
    setWorkouts(updated.filter(x => x.userId === auth.id));
  };

  return (
    <div className="container">
      <h2>My Workouts</h2>
      <div className="list" style={{ marginTop: 12 }}>
        {workouts.map(w => (
          <div className="list-item" key={w.id}>
            <div>
              <strong>{w.exercise}</strong>
              <div className="helper">{w.sets} sets × {w.reps} reps • {w.duration} min</div>
            </div>
            <div>
              {w.done ? <span className="badge">Completed</span> : <button className="btn-success" onClick={() => markDone(w.id)}>Mark Done</button>}
            </div>
          </div>
        ))}
        {!workouts.length && <div style={{ padding: 12 }}>No workouts assigned.</div>}
      </div>
    </div>
  );
}

export default UserWorkouts;
