import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";

function LoginForm({ onLogin }) {
  const [email, setEmail] = useState("harini@example.com");
  const [password, setPassword] = useState("password");
  const [role, setRole] = useState("user");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8080/api/auth/login", {
        email,
        password,
        role,
      });

      const user = res.data;
      onLogin(user); // ✅ store user in app state

      // ✅ Use role returned from backend instead of dropdown
      const userRole = user.role?.toLowerCase() || role;
      navigate(userRole === "user" ? "/user" : userRole === "trainer" ? "/trainer" : "/admin");
    } catch (err) {
      if (err.response) {
        const message = typeof err.response.data === "string"
          ? err.response.data
          : err.response.data.message || "Login failed";
        alert(`Login failed: ${message}`);
      } else {
        alert("Server error, please try again later.");
      }
    }
  };

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: 420, margin: "40px auto" }}>
        <h2 style={{ fontSize: 30, textAlign: "center" }}>Login</h2>
        <form onSubmit={handleSubmit}>
          <label>Email</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

          <label>Password</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />

          <label>Role</label>
          <select value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="user">User</option>
            <option value="trainer">Trainer</option>
            <option value="admin">Admin</option>
          </select>

          <div style={{ display: "flex", justifyContent: "space-between", marginTop: 35, marginBottom: 20 }}>
            <button type="submit">Login</button>
            <Link to="/register"><button type="button" className="btn-secondary">Register</button></Link>
          </div>
        </form>
      </div>
    </div>
  );
}

export default LoginForm;
