import React, { useEffect, useState } from "react";
import axios from "axios";

function TrainerProfile({ auth }) {
  const [form, setForm] = useState({
    name: auth.name || "",
    email: auth.email || "",
    specialization: auth.specialization || "",
    availability: "Mon-Fri",
  });
  const [users, setUsers] = useState([]);
  const [clients, setClients] = useState(auth.clients || []);
  const [addClientId, setAddClientId] = useState("");

  useEffect(() => {
    // Load all users to map as clients
    axios.get("http://localhost:8080/api/users")
      .then(res => setUsers(res.data))
      .catch(err => console.error("Error fetching users:", err));

    // Load trainer profile (optional if you have trainer entity)
    setClients(auth.clients || []);
  }, [auth]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const saveProfile = (e) => {
    e.preventDefault();
    // If you have a Trainer entity, call PUT /api/trainers/{id}
    // Otherwise, you can store specialization/availability in User (role=trainer)
    axios.put(`http://localhost:8080/api/users/${auth.id}`, {
      ...form,
      role: "trainer",
      id: auth.id
    })
      .then(() => alert("Trainer profile updated!"))
      .catch(err => {
        console.error("Error updating trainer profile:", err);
        alert("Update failed");
      });
  };

  const addClient = () => {
    if (!addClientId) return;
    if (clients.includes(Number(addClientId))) return alert("Client already mapped.");
    const updatedClients = [...clients, Number(addClientId)];
    setClients(updatedClients);

    // Persist mapping—if you have a mapping table, call POST /api/trainer-clients
    axios.post("http://localhost:8080/api/trainer-clients", {
      trainerId: auth.id,
      userId: Number(addClientId)
    })
      .then(() => alert("Client added to your list."))
      .catch(err => {
        console.error("Error adding client:", err);
        alert("Failed to add client");
      });

    setAddClientId("");
  };

  const removeClient = (id) => {
    const updatedClients = clients.filter(c => c !== id);
    setClients(updatedClients);

    axios.delete(`http://localhost:8080/api/trainer-clients`, {
      data: { trainerId: auth.id, userId: id }
    })
      .then(() => alert("Client removed."))
      .catch(err => {
        console.error("Error removing client:", err);
        alert("Failed to remove client");
      });
  };

  return (
    <div className="container">
      <h2>Trainer Profile</h2>
      <div className="card" style={{ marginTop: 12 }}>
        <form onSubmit={saveProfile}>
          <label>Name</label>
          <input name="name" value={form.name} onChange={handleChange} required />
          <label>Email</label>
          <input type="email" name="email" value={form.email} onChange={handleChange} required />
          <label>Specialization</label>
          <input name="specialization" value={form.specialization} onChange={handleChange} placeholder="e.g., Weight Loss" />
          <label>Availability</label>
          <input name="availability" value={form.availability} onChange={handleChange} />
          <div style={{ marginTop: 12 }}>
            <button type="submit">Save Profile</button>
          </div>
        </form>
      </div>

      <div className="card" style={{ marginTop: 12 }}>
        <h3>Manage Clients</h3>
        <div className="row">
          <div className="col">
            <label>Add client</label>
            <select value={addClientId} onChange={(e) => setAddClientId(e.target.value)}>
              <option value="">Select user</option>
              {users.map(u => <option key={u.id} value={u.id}>{u.name} ({u.email})</option>)}
            </select>
            <div style={{ marginTop: 8 }}>
              <button className="btn-secondary" onClick={addClient}>Add Client</button>
            </div>
          </div>
          <div className="col">
            <div className="list">
              {clients.map(id => {
                const u = users.find(x => x.id === id);
                return (
                  <div className="list-item" key={id}>
                    <div><strong>{u?.name}</strong> <span className="helper">({u?.email})</span></div>
                    <button className="btn-danger" onClick={() => removeClient(id)}>Remove</button>
                  </div>
                );
              })}
              {!clients.length && <div style={{ padding: 12 }}>No clients mapped yet.</div>}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TrainerProfile;
