import React, { useEffect, useState } from "react";

function TrainerWorkouts({ auth }) {
  const [exercise, setExercise] = useState("");
  const [sets, setSets] = useState("");
  const [reps, setReps] = useState("");
  const [duration, setDuration] = useState("");
  const [assignedUser, setAssignedUser] = useState("");
  const [clients, setClients] = useState([]);

  useEffect(() => {
    const users = JSON.parse(localStorage.getItem("users") || "[]");
    const myClients = users.filter(u => (auth.clients || []).includes(u.id));
    setClients(myClients);
  }, [auth.clients]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!assignedUser) return alert("Select a client");
    const list = JSON.parse(localStorage.getItem("workouts") || "[]");
    const newItem = {
      id: "w" + (list.length + 1),
      userId: assignedUser,
      exercise,
      sets: Number(sets),
      reps: Number(reps),
      duration: Number(duration) || 0,
      done: false,
    };
    localStorage.setItem("workouts", JSON.stringify([...list, newItem]));
    setExercise(""); setSets(""); setReps(""); setDuration("");
    alert("Workout assigned!");
  };

  return (
    <div className="container">
      <h2>Create Workout Routine</h2>
      <div className="card" style={{ marginTop: 12 }}>
        <form onSubmit={handleSubmit}>
          <label>Exercise</label>
          <input value={exercise} onChange={(e) => setExercise(e.target.value)} required />
          <div className="row">
            <div className="col">
              <label>Sets</label>
              <input type="number" value={sets} onChange={(e) => setSets(e.target.value)} required />
            </div>
            <div className="col">
              <label>Reps</label>
              <input type="number" value={reps} onChange={(e) => setReps(e.target.value)} required />
            </div>
            <div className="col">
              <label>Duration (min)</label>
              <input type="number" value={duration} onChange={(e) => setDuration(e.target.value)} />
            </div>
          </div>
          <label>Assign to Client</label>
          <select value={assignedUser} onChange={(e) => setAssignedUser(e.target.value)}>
            <option value="">Select client</option>
            {clients.map(c => <option key={c.id} value={c.id}>{c.name} ({c.email})</option>)}
          </select>
          <div style={{ marginTop: 12 }}>
            <button type="submit">Create Workout</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default TrainerWorkouts;
