import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Register() {
  const navigate = useNavigate();
  const [form, setForm] = useState({ role: "user", name: "", email: "", password: "" });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    const store = form.role === "user" ? "users" : form.role === "trainer" ? "trainers" : "admins";
    const list = JSON.parse(localStorage.getItem(store) || "[]");
    list.push({ id: store[0] + (list.length + 1), ...form, clients: form.role === "trainer" ? [] : undefined });
    localStorage.setItem(store, JSON.stringify(list));
    alert("Registered successfully! Please login.");
    navigate("/"); // ✅ navigate back to login
  };

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: 520, margin: "20px auto" }}>
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <label>Role</label>
          <select name="role" value={form.role} onChange={handleChange}>
            <option value="user">User</option>
            <option value="trainer">Trainer</option>
            <option value="admin">Admin</option>
          </select>
          <label>Name</label>
          <input name="name" value={form.name} onChange={handleChange} required />
          <label>Email</label>
          <input type="email" name="email" value={form.email} onChange={handleChange} required />
          <label>Password</label>
          <input type="password" name="password" value={form.password} onChange={handleChange} required />
          <div style={{ marginTop: 12 }}>
            <button type="submit">Register</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Register;
